mapper.select:
    	select a.subtype_id,a.prod_id,b.text as subtype_name,c.text as prod_name,sum(a.num) as num
	      from 
	      (select x.*,y.num from ems_equipment x, ems_storeequipment y where x.ecode=y.ecode and y.store_id=#{store_id}) a
	    inner join ems_equipmentsubtype b on a.subtype_id=b.id
			inner join ems_equipmentprod c on a.prod_id=c.id
			inner join ems_brand d on a.brand_id=d.id
			inner join ems_supplier e on a.supplier_id=e.id
		
			
				and b.id=#{subtype_id}
			
			
				and c.id=#{prod_id}
			
			
				and d.id=#{brand_id}
			
			
				and e.id=#{supplier_id}
			
		
		 group by a.subtype_id,a.prod_id,b.text,c.text

    	select a.*,b.text as subtype_name,c.text as prod_name,d.name as brand_name,d.name as supplier_name 
    	from (select x.* from ems_equipment x, ems_storeequipment y where x.ecode=y.ecode and y.store_id=#{store_id} and y.num>0) a 
		inner join ems_equipmentsubtype b on a.subtype_id=b.id
		inner join ems_equipmentprod c on a.prod_id=c.id
		inner join ems_brand d on a.brand_id=d.id
		inner join ems_supplier e on a.supplier_id=e.id
		
			
				and b.id=#{subtype_id}
			
			
				and c.id=#{prod_id}
			
			
				and d.id=#{brand_id}
			
			
				and e.id=#{supplier_id}
