mapper.select:
    	select a.subtype_id,a.prod_id,b.name as subtype_name,c.name as prod_name,sum(a.num) as num
    	from (select x.*,1 as num from ems_equipment x where x.workUnit_id=#{workUnit_id}) a
		inner join ems_equipmentsubtype b on a.subtype_id=b.id
		inner join ems_equipmentprod c on a.prod_id=c.id
		inner join ems_brand d on a.brand_id=d.id
		inner join ems_supplier e on a.supplier_id=e.id


				and b.id=#{subtype_id}


				and c.id=#{prod_id}


				and d.id=#{brand_id}


				and e.id=#{supplier_id}


		 group by a.subtype_id,a.prod_id,b.name,c.name

select a.subtype_id,a.prod_id,
a.ecode,
a.style,b.name as subtype_name,c.name as prod_name,c.spec as prod_spec,d.name as brand_name,e.name as supplier_name ,sum(a.num) as num from (select x.*,1 as num from ems_equipment x where x.workUnit_id=#{workUnit_id}) a inner join ems_equipmentsubtype b on a.subtype_id=b.id inner join ems_equipmentprod c on a.prod_id=c.id inner join ems_brand d on a.brand_id=d.id inner join ems_supplier e on a.supplier_id=e.id
and c.id=#{prod_id}
and a.style=#{style}
and d.id=#{brand_id}
and e.id=#{supplier_id}
group by a.subtype_id,a.prod_id,
a.ecode,
a.style,b.name ,c.name,c.spec,d.name,e.name
