mapper.select:
    	select a.*,b.name as store_name,c.name as workUnit_name,d.name as project_name,'out' as type from ems_borrow a,ems_store b,ems_workunit c,ems_project d
		where a.store_id=b.id and a.workunit_id=c.id and a.project_id=d.id

			and to_char(a.operateDate,'yyyy-mm-dd') >=#{operateDate_start}


			and to_char(a.operateDate,'yyyy-mm-dd') <=#{operateDate_end}


			and a.store_id =#{store_id}


			and a.isAllReturn =#{isAllReturn}


			and a.workUnit_id =#{workUnit_id}


			and a.project_id =#{project_id}

    	select count(a.id) from ems_borrow a


			and to_char(a.operateDate,'yyyy-mm-dd') >=#{operateDate_start}


			and to_char(a.operateDate,'yyyy-mm-dd') <=#{operateDate_end}


			and a.store_id =#{store_id}


			and a.isAllReturn =#{isAllReturn}


			and a.workUnit_id =#{workUnit_id}


			and a.project_id =#{project_id}

mapper.update:
update ems_borrow set isallreturn=(
       select decode(count(id) ,sum(decode(isreturn,'Y',1,0)),'Y','N')  from ems_borrowlist 
       where borrow_id=#{borrow_id}
) where id=#{borrow_id}
