mapper.select:
    	select a.*,b.name as store_name,c.name as workUnit_name,'in' as type from ems_installin a,ems_store b,ems_workunit c
		where a.store_id=b.id and a.workunit_id=c.id
		
			and a.operateDate >=#{operateDate_start}
		
		
			and a.operateDate <=#{operateDate_end}
		
		
			and a.store_id =#{store_id}
		
		
			and a.workUnit_id =#{workUnit_id}

    	select a.*,b.name as store_name,c.name as workUnit_name,'out' as type from ems_installout a,ems_store b,ems_workunit c
		where a.store_id=b.id and a.workunit_id=c.id
		
			and a.operateDate >=#{operateDate_start}
		
		
			and a.operateDate <=#{operateDate_end}
		
		
			and a.store_id =#{store_id}
		
		
			and a.workUnit_id =#{workUnit_id}
