mapper.select:
    	select a.*,b.name as store_name,c.name as workUnit_name from ems_installin a,ems_store b,ems_workunit c
		where a.store_id=b.id and a.workunit_id=c.id

			and to_char(a.operateDate,'yyyy-mm-dd') >=#{operateDate_start}


			and to_char(a.operateDate,'yyyy-mm-dd') <=#{operateDate_end}


			and a.store_id =#{store_id}


			and a.workUnit_id =#{workUnit_id}


			and a.type =#{type}
