mapper.select:
select a.*, b.name as area_name, c.id as workunit_id, c.name as workunit_name, d.name as customer_name, e.task_num from EMS_pole a inner join ems_area b on a.area_id=b.id inner join Ems_Workunit c on b.workunit_id=c.id inner join ems_customer d on a.customer_id=d.id left join (select pole_id,count(id) as task_num from ems_task where status!='complete' group by pole_id) e on a.id=e.pole_id
and a.customer_id=#{customer_id}
and a.area_id=#{area_id}
and b.workunit_id=#{workunit_id}
and (a.name like #{pole_name} or a.code like #{pole_name})

    	select 
			count(a.id)
		from EMS_pole a 
		inner join ems_area b on  a.area_id=b.id


				and a.customer_id=#{customer_id}


				and a.area_id=#{area_id}


				and b.workunit_id=#{workunit_id}


				and (a.name like #{pole_name} or a.code like #{pole_name})

select a.*, b.name as area_name, c.id as workunit_id, c.name as workunit_name, d.name as customer_name, 0 as task_num from EMS_pole a inner join ems_area b on a.area_id=b.id inner join Ems_Workunit c on b.workunit_id=c.id inner join ems_customer d on a.customer_id=d.id
not exists ( select 1 from ems_task task where task.pole_id=a.id)
and a.customer_id=#{customer_id}
and a.area_id=#{area_id}
and b.workunit_id=#{workunit_id}
and (a.name like #{pole_name} or a.code like #{pole_name})

    	select 
			count(a.id)
		from EMS_pole a 
		inner join ems_area b on  a.area_id=b.id

			not exists ( select 1 from ems_task task where  task.pole_id=a.id)

				and a.customer_id=#{customer_id}


				and a.area_id=#{area_id}


				and b.workunit_id=#{workunit_id}


				and (a.name like #{pole_name} or a.code like #{pole_name})

    	select a.*,b.code as pole_code from ems_task a,ems_pole b

    		a.pole_id=b.id

				and a.customer_id=#{customer_id}


				and a.status=#{status}


				and a.type=#{type}


				and a.workunit_id=#{workunit_id}


				and a.pole_id=#{pole_id} and  a.status  < >'complete'


				and (a.pole_name like #{pole_name} or b.code like #{pole_name} )


				and a.status in ('newTask','read') and floor(to_number(sysdate-(a.createDate+0))*24*60) > #{handling}

    	select a.*,b.code as pole_code from ems_task a,ems_pole b
		where a.pole_id=b.id
			and a.type='repair'
			and a.status !='complete'

				and a.workunit_id=#{workunit_id}


				and a.customer_id=#{customer_id}

select * from ems_task a
where a.status in ('newTask','read') and floor(to_number(sysdate-(a.createDate+0))*24*60) > #{handling}
and not exists (select 1 from ems_message b where a.id=b.task_id and floor(to_number((a.createdate+0)-(b.createDate+0))*24*60) < #{handling})

    	select ecode from ems_equipment where status not in (2,3,4)
		and ecode in 

		 #{item}

    	select a.id from ems_task a,ems_taskequipmentlist b 
		where a.status in ('handling','submited')
		and a.id=b.task_id
		and a.workunit_id=#{workunit_id}
		and b.ecode=#{ecode}
