mapper.select:
    	select a.*,b.id as scrap_id,d.name as prod_name,d.style as equipment_style from ems_repair a 
    	left join ems_scrap b on a.id=b.repair_id
    	left join ems_equipmentprod d on a.prod_id=d.id

			a.str_out_id in (select store_id from sys_userstore c where c.user_id=#{user_id} and c.edit='Y')

				and a.str_out_id=#{str_out_id}


				and a.rpa_id=#{rpa_id}


				and to_char(a.str_out_date,'yyyy-mm-dd') >= #{str_out_date_start}


				and to_char(a.str_out_date,'yyyy-mm-dd') <= #{str_out_date_end}


				and a.ecode=#{ecodes}


				and a.status=#{status}


	    		and b.id is not null


		order by a.status

    	select a.*,d.name as prod_name,d.style as equipment_style,e.name as workunit_name  from ems_repair a
    	left join ems_equipmentprod d on a.prod_id=d.id
    	left join ems_workunit e on a.workunit_id=e.id

			a.rpa_id in (select store_id from sys_userstore c where c.user_id=#{user_id} and c.edit='Y')

				and a.str_out_id=#{str_out_id}


				and a.rpa_id=#{rpa_id}


				and to_char(a.str_out_date,'yyyy-mm-dd') >= #{str_out_date_start}


				and to_char(a.str_out_date,'yyyy-mm-dd') <= #{str_out_date_end}


				and a.ecode=#{ecodes}


				and a.status=#{status}


		order by a.status

    	select a.*,b.id as scrap_id,d.name as workunit_name,e.name as rpa_user_name 
    	 ,f.name as prod_name,f.style as equipment_style
    	 from ems_repair a 
    	 left join ems_scrap b on a.id=b.repair_id
    	 left join ems_workunit d on a.workunit_id=d.id
    	 left join sys_user e on a.rpa_user_id=e.id
    	 left join ems_equipmentprod f on a.prod_id=f.id

			a.rpa_id in (select store_id from sys_userstore c where c.user_id=#{user_id} and c.edit='Y')

				and a.str_out_id=#{str_out_id}


				and a.rpa_id=#{rpa_id}


				and to_char(a.rpa_in_date,'yyyy-mm-dd') >= #{rpa_in_date_start}


				and to_char(a.rpa_in_date,'yyyy-mm-dd') <= #{rpa_in_date_end}


				and a.ecode=#{ecodes}


				and a.status=#{status}


	    		and b.id is not null


		order by a.status
