mapper.select:
select a.subtype_id,b.name as subtype_name,a.prod_id,c.name as prod_name,c.style as prod_style from ( select c.id,c.name,b.ecode,d.subtype_id,d.prod_id from ems_pole a,ems_equipment_pole b,ems_customer c,ems_equipment d where a.id=b.pole_id and a.customer_id=c.id and b.ecode=d.ecode and exists (select 1 from sys_user_workunit x where x.workunit_id=a.workunit_id and x.user_id=#{user_id}) and c.parent_id=#{customer_2} and c.type!=2
and c.id=#{customer_0or1}
) a,ems_equipmentsubtype b,ems_equipmentprod c where a.subtype_id=b.id and a.prod_id=c.id group by a.subtype_id,b.name,a.prod_id,c.name,c.style order by a.subtype_id,a.prod_id

select a.id as customer_id,a.name as customer_name,a.prod_id,c.name as prod_name,count(a.ecode) num,sum(a.value_net) value_net from ( select c.id,c.name,b.ecode,d.prod_id ,g.value_net from ems_pole a inner join ems_equipment_pole b on a.id=b.pole_id inner join ems_customer c on a.customer_id=c.id inner join ems_equipment d on b.ecode=d.ecode left join report_assetclean g on b.ecode=g.ecode where exists (select 1 from sys_user_workunit x where x.workunit_id=a.workunit_id and x.user_id=#{user_id}) and c.parent_id=#{customer_2} and c.type!=2
and c.id=#{customer_0or1}
) a,ems_equipmentprod c where a.prod_id=c.id group by a.id,a.name,a.prod_id,c.name order by a.id,a.prod_id

select a.subtype_id,b.name as subtype_name,a.prod_id,c.name as prod_name,c.style as prod_style from ( select a.id,a.name,b.ecode,d.subtype_id,d.prod_id from ems_pole a,ems_equipment_pole b,ems_customer c,ems_equipment d where a.id=b.pole_id and a.customer_id=c.id and b.ecode=d.ecode and exists (select 1 from sys_user_workunit x where x.workunit_id=a.workunit_id and x.user_id=#{user_id}) and c.parent_id=#{customer_2} and c.type!=2 and a.poleType='machineroom'
and c.id=#{customer_0or1}
) a,ems_equipmentsubtype b,ems_equipmentprod c where a.subtype_id=b.id and a.prod_id=c.id group by a.subtype_id,b.name,a.prod_id,c.name,c.style order by a.subtype_id,a.prod_id

select a.id as customer_id,a.name as customer_name,a.prod_id,c.name as prod_name,count(a.ecode) num,sum(a.value_net) value_net from ( select a.id,a.name,b.ecode,d.prod_id,g.value_net from ems_pole a inner join ems_equipment_pole b on a.id=b.pole_id inner join ems_customer c on a.customer_id=c.id inner join ems_equipment d on b.ecode=d.ecode left join report_assetclean g on b.ecode=g.ecode where exists (select 1 from sys_user_workunit x where x.workunit_id=a.workunit_id and x.user_id=#{user_id}) and c.parent_id=#{customer_2} and c.type!=2 and a.poleType='machineroom'
and c.id=#{customer_0or1}
) a,ems_equipmentprod c where a.prod_id=c.id group by a.id,a.name,a.prod_id,c.name order by a.id,a.prod_id
