mapper.select:
select a.subtype_id,b.name as subtype_name,a.prod_id,c.name as prod_name from ( select c.id,c.name,b.ecode,d.subtype_id,d.prod_id from ems_pole a,ems_equipment_pole b,ems_customer c,ems_equipment d where a.id=b.pole_id and a.customer_id=c.id and b.ecode=d.ecode and c.parent_id=#{customer_2} and c.type!=2
and c.id=#{customer_0or1}
) a,ems_equipmentsubtype b,ems_equipmentprod c where a.subtype_id=b.id and a.prod_id=c.id group by a.subtype_id,b.name,a.prod_id,c.name order by a.subtype_id,a.prod_id

select a.id as customer_id,a.name as customer_name,a.prod_id,c.name as prod_name,count(a.ecode) num from ( select c.id,c.name,b.ecode,d.prod_id from ems_pole a,ems_equipment_pole b,ems_customer c,ems_equipment d where a.id=b.pole_id and a.customer_id=c.id and b.ecode=d.ecode and c.parent_id=#{customer_2} and c.type!=2
and c.id=#{customer_0or1}
) a,ems_equipmentprod c where a.prod_id=c.id group by a.id,a.name,a.prod_id,c.name order by a.id,a.prod_id

select a.subtype_id,b.name as subtype_name,a.prod_id,c.name as prod_name from ( select a.id,a.name,b.ecode,d.subtype_id,d.prod_id from ems_pole a,ems_equipment_pole b,ems_customer c,ems_equipment d where a.id=b.pole_id and a.customer_id=c.id and b.ecode=d.ecode and c.parent_id=#{customer_2} and c.type!=2 and a.poleType='machineroom'
and c.id=#{customer_0or1}
) a,ems_equipmentsubtype b,ems_equipmentprod c where a.subtype_id=b.id and a.prod_id=c.id group by a.subtype_id,b.name,a.prod_id,c.name order by a.subtype_id,a.prod_id

select a.id as customer_id,a.name as customer_name,a.prod_id,c.name as prod_name,count(a.ecode) num from ( select a.id,a.name,b.ecode,d.prod_id from ems_pole a,ems_equipment_pole b,ems_customer c,ems_equipment d where a.id=b.pole_id and a.customer_id=c.id and b.ecode=d.ecode and c.parent_id=#{customer_2} and c.type!=2 and a.poleType='machineroom'
and c.id=#{customer_0or1}
) a,ems_equipmentprod c where a.prod_id=c.id group by a.id,a.name,a.prod_id,c.name order by a.id,a.prod_id
