mapper.select:
    	select a.*, d.name as subtype_name,e.name as brand_name,c.name as prod_name,c.style as prod_style,c.unit as prod_unit 
    	from (
    		select a.id as instore_id,d.project_id,a.store_id,to_char(a.operatedate,'yyyy-mm-dd') operatedate,b.ecode,'新品入库' as instoretype,a.memo
		    from ems_instore a 
		    inner join ems_instorelist b on a.id=b.instore_id
		    inner join ems_orderlist c on b.orderlist_id=c.id
		    inner join ems_order d on c.order_id=d.id
		    where  to_char(a.operatedate,'yyyy-mm-dd') >=#{date_start}  and to_char(a.operatedate,'yyyy-mm-dd') <=#{date_end}
			
				and a.store_id=#{store_id}
			
			
				and d.project_id=#{project_id}
			
		    union all
		    select a.id as instore_id,a.project_id,a.str_in_id as store_id,to_char(b.indate,'yyyy-mm-dd') operatedate,b.ecode,'调拨入库' as instoretype,a.memo
		    from ems_adjust a 
		    inner join ems_adjustlist b on a.id=b.adjust_id
		    where  b.adjustListStatus='in' 
		     and to_char(b.indate,'yyyy-mm-dd') >=#{date_start}  and to_char(b.indate,'yyyy-mm-dd') <=#{date_end}
			
				and a.str_in_id=#{store_id}
			
			
				and a.project_id=#{project_id}
			
		    union all
		    select a.id as instore_id,b.project_id,a.store_id,to_char(a.operatedate,'yyyy-mm-dd') operatedate,b.ecode,'借用归还' as instoretype,a.memo
		    from ems_installin a 
		    inner join ems_installinlist b on a.id=b.installin_id
		    where  b.installinlisttype='installout' 
		    and to_char(a.operatedate,'yyyy-mm-dd') >=#{date_start}  and to_char(a.operatedate,'yyyy-mm-dd') <=#{date_end}
			
				and a.store_id=#{store_id}
			
			
				and a.project_id=#{project_id}
			
		    union all
		    select a.id as instore_id,b.project_id,a.store_id,to_char(a.operatedate,'yyyy-mm-dd') operatedate,b.ecode,'拆回入库' as instoretype,a.memo
		    from ems_installin a 
		    inner join ems_installinlist b on a.id=b.installin_id
		    where  b.installinlisttype='takedown'
		    and to_char(a.operatedate,'yyyy-mm-dd') >=#{date_start}  and to_char(a.operatedate,'yyyy-mm-dd') <=#{date_end}
			
				and a.store_id=#{store_id}
			
			
				and a.project_id=#{project_id}
			
		    union all
		    select a.id as instore_id,a.project_id,a.store_id,to_char(b.returndate,'yyyy-mm-dd') operatedate,b.ecode,'借用归还' as instoretype,a.memo
		    from ems_borrow a 
		    inner join ems_borrowlist b on a.id=b.borrow_id
		    where  b.isreturn='Y' 
		    and to_char(b.returndate,'yyyy-mm-dd') >=#{date_start}  and to_char(b.returndate,'yyyy-mm-dd') <=#{date_end}
			
				and a.store_id=#{store_id}
			
			
				and a.project_id=#{project_id}
			
		    union all
		    select a.id as instore_id,'修复入库' project_id,a.str_in_id as store_id,to_char(a.str_in_date,'yyyy-mm-dd') operatedate,a.ecode,'修复入库' as instoretype,a.memo
		    from ems_repair a 
		    where  a.status='over' 
		    and to_char(a.str_in_date,'yyyy-mm-dd') >=#{date_start}  and to_char(a.str_in_date,'yyyy-mm-dd') <=#{date_end}
		    
				and a.str_in_id=#{store_id}
			
			
				and a.project_id=#{project_id}
			
    	
    	) a,ems_equipment b,ems_equipmentprod c,ems_equipmentsubtype d,ems_brand e
		where a.ecode=b.ecode and b.prod_id=c.id and b.subtype_id=d.id and b.brand_id=e.id
		order by a.operatedate

    	select a.*, d.name as subtype_name,e.name as brand_name,c.name as prod_name,c.style as prod_style,c.unit as prod_unit 
    	from (
		select a.id as installout_id,a.project_id,a.store_id,to_char(a.operatedate,'yyyy-mm-dd') operatedate,b.ecode,c.name as workunit_name,d.name as pole_name,d.customer_id,'领用' as installouttype,a.memo
		from ems_installout a 
		inner join ems_installoutlist b on a.id=b.installout_id
		inner join ems_workunit c on a.workunit_id=c.id
		inner join ems_pole d on b.pole_id=d.id
		where  b. installoutlisttype='installout' and b.isreturn='N'
		and to_char(a.operatedate,'yyyy-mm-dd') >=#{date_start}  and to_char(a.operatedate,'yyyy-mm-dd') <=#{date_end}
		
			and a.store_id=#{store_id}
		
		
			and a.project_id=#{project_id}
		
		union all
		select a.id as installout_id,a.project_id,a.store_id,to_char(a.operatedate,'yyyy-mm-dd') operatedate,b.ecode,c.name as workunit_name,d.name as pole_name,d.customer_id,'借转领' as installouttype,a.memo
		from ems_borrow a 
		inner join ems_borrowlist b on a.id=b.borrow_id
		inner join ems_workunit c on a.workunit_id=c.id
		inner join ems_pole d on b.pole_id=d.id
		where  b. borrowlisttype='installout' and b.isreturn='N'
		and to_char(a.operatedate,'yyyy-mm-dd') >=#{date_start}  and to_char(a.operatedate,'yyyy-mm-dd') <=#{date_end}
		
			and a.store_id=#{store_id}
		
		
			and a.project_id=#{project_id}
		
		union
		select a.id as installout_id,a.project_id,a.str_out_id as store_id,to_char(a.str_out_date,'yyyy-mm-dd') as operatedate,b.ecode,c.name as workunit_name,'' as pole_name,'' as customer_id,'调拨领用' as installouttype,a.memo
		from ems_adjust a 
		inner join ems_adjustlist b on a.id=b.adjust_id
		inner join ems_store c on a.str_out_id=c.id
		where  a.adjusttype in ('installout','returnback')
		and to_char(a.str_out_date,'yyyy-mm-dd') >=#{date_start}  and to_char(a.str_out_date,'yyyy-mm-dd') <=#{date_end}
		
			and a.str_out_id=#{store_id}
		
		
			and a.project_id=#{project_id}
			
		) a,ems_equipment b,ems_equipmentprod c,ems_equipmentsubtype d,ems_brand e
		where a.ecode=b.ecode and b.prod_id=c.id and b.subtype_id=d.id and b.brand_id=e.id
		order by a.operatedate

    	select a.*, d.name as subtype_name,e.name as brand_name,c.name as prod_name,c.style as prod_style,c.unit as prod_unit 
    	from (
    	select a.id as borrow_id,a.project_id,to_char(a.operatedate,'yyyy-mm-dd') operatedate,b.ecode,a.store_id,c.name as workunit_name,decode(b.isreturn,'Y','已归还','未归还') as status,'领用' as borrowtype,a.memo
		from ems_installout a 
		inner join ems_installoutlist b on a.id=b.installout_id
		inner join ems_workunit c on a.workunit_id=c.id
		where  b.installoutlisttype='borrow' 
		and to_char(a.operatedate,'yyyy-mm-dd') >=#{date_start}  and to_char(a.operatedate,'yyyy-mm-dd') <=#{date_end}
		
			and a.store_id=#{store_id}
		
		
			and a.project_id=#{project_id}
		
		union all
		select a.id as borrow_id,a.project_id,to_char(a.operatedate,'yyyy-mm-dd') operatedate,b.ecode,a.store_id,c.name as workunit_name,decode(b.isreturn,'Y','已归还','未归还') as status,'借用' as borrowtype,a.memo
		from ems_borrow a 
		inner join ems_borrowlist b on a.id=b.borrow_id
		inner join ems_workunit c on a.workunit_id=c.id
		where  b.borrowlisttype='borrow' 
		and to_char(a.operatedate,'yyyy-mm-dd') >=#{date_start}  and to_char(a.operatedate,'yyyy-mm-dd') <=#{date_end}
		
			and a.store_id=#{store_id}
		
		
			and a.project_id=#{project_id}
		
		union all
		select a.id as borrow_id,a.project_id,to_char(a.str_out_date,'yyyy-mm-dd') operatedate,b.ecode,a.str_out_id as store_id,c.name as workunit_name,decode(b.isreturn,'Y','已归还','未归还') as status,'调拨' as borrowtype,a.memo
		from ems_adjust a 
		inner join ems_adjustlist b on a.id=b.adjust_id
		inner join ems_store c on a.str_out_id=c.id
		where  a.adjusttype='borrow'
		and to_char(a.str_out_date,'yyyy-mm-dd') >=#{date_start}  and to_char(a.str_out_date,'yyyy-mm-dd') <=#{date_end}
		
			and a.str_out_id=#{store_id}
		
		
			and a.project_id=#{project_id}
		
		) a,ems_equipment b,ems_equipmentprod c,ems_equipmentsubtype d,ems_brand e
		where a.ecode=b.ecode and b.prod_id=c.id and b.subtype_id=d.id and b.brand_id=e.id
		order by a.operatedate

select a.store_id,a.installouttype_name,a.installouttype_content, e.id as type_id,e.name as type_name,d.id as subtype_id,d.name as subtype_name,c.id as prod_id,c.name as prod_name,c.unit as prod_unit,c.style prod_style,f.name as brand_name,count(a.installoutlist_id) as installoutnum from ( select a.store_id,b.ecode,c.name as installouttype_name,b.installouttype_content,b.id as installoutlist_id from ems_installout a,ems_installoutlist b,ems_installouttype c where a.id=b.installout_id and b.installouttype_id=c.id and b.installoutlisttype='installout'
and a.store_id=#{store_id}
and a.store_id in (${store_id})
and to_char(b.returnDate,'yyyy-mm-dd') >=#{date_start} and to_char(b.returnDate,'yyyy-mm-dd') <=#{date_end} union all select a.store_id,b.ecode,c.name as installouttype_name,b.installouttype_content,b.id as installoutlist_id from ems_borrow a,ems_borrowlist b,ems_installouttype c where a.id=b.borrow_id and b.installouttype_id=c.id and b.borrowlisttype='installout'
and a.store_id=#{store_id}
and a.store_id in (${store_id})
and to_char(b.returnDate,'yyyy-mm-dd') >=#{date_start} and to_char(b.returnDate,'yyyy-mm-dd') <=#{date_end} union all select a.str_out_id store_id,b.ecode,'调拨' installouttype_name,c.name as installouttype_content,b.id as installoutlist_id from ems_adjust a,ems_adjustlist b,ems_store c where a.id=b.adjust_id and a.str_in_id=c.id and a.adjusttype in ('installout','returnback')
and a.str_out_id=#{store_id}
and a.str_out_id in (${store_id})
and to_char(a.str_out_date,'yyyy-mm-dd') >=#{date_start} and to_char(a.str_out_date,'yyyy-mm-dd') <=#{date_end} ) a inner join ems_equipment b on a.ecode=b.ecode inner join ems_equipmentprod c on b.prod_id=c.id inner join ems_equipmentsubtype d on c.subtype_id=d.id inner join ems_equipmenttype e on d.parent_id=e.id inner join ems_brand f on c.brand_id=f.id group by a.store_id,a.installouttype_name ,a.installouttype_content, e.id,e.name ,d.id,d.name,c.id,c.name ,c.unit,c.style ,f.name order by installouttype_name,installouttype_content
