mapper.select:
    	select a.*,b.code as pole_code from ems_task a,ems_pole b
		where a.pole_id=b.id
			and exists (select 1 from sys_user_workunit x where x.workunit_id=b.workunit_id and x.user_id=#{user_id})
			and a.type='repair' and a.status!='complete'

				and a.workunit_id=#{workunit_id}


				and a.customer_id=#{customer_id}


				and to_char(a.createDate,'yyyy-mm-dd')>=#{date_start}


				and to_char(a.createDate,'yyyy-mm-dd')<=#{date_end}

    	select a.*,b.code as pole_code from ems_task a,ems_pole b
		where a.pole_id=b.id
			and exists (select 1 from sys_user_workunit x where x.workunit_id=b.workunit_id and x.user_id=#{user_id})
			and a.type='repair'
			and a.status !='complete'

				and a.workunit_id=#{workunit_id}


				and a.customer_id=#{customer_id}


				and to_char(a.createDate,'yyyy-mm-dd')>=#{date_start}


				and to_char(a.createDate,'yyyy-mm-dd')<=#{date_end}

    	select a.*,b.code as pole_code, c.name as handleMethod_name
		,case when  a.status='complete' then '已修复' else '未修复' end  as result
		from ems_task a
		inner join ems_pole b on a.pole_id=b.id
		left join ems_handlemethod c on a.handleMethod_id=c.id
		where 
		exists (select 1 from sys_user_workunit x where x.workunit_id=b.workunit_id and x.user_id=#{user_id})
		and a.type='repair'
		and to_char(a.createDate,'yyyy-mm-dd')>=#{date_start}
		and to_char(a.createDate,'yyyy-mm-dd')<=#{date_end}

			and b.code =#{pole_code}


			and a.hitchType_id =#{hitchType_id}

    	select count(a.id)
		from ems_task a
		inner join ems_pole b on a.pole_id=b.id
		where a.type='repair'
		and exists (select 1 from sys_user_workunit x where x.workunit_id=b.workunit_id and x.user_id=#{user_id})
		and to_char(a.createDate,'yyyy-mm-dd')>=#{date_start}
		and to_char(a.createDate,'yyyy-mm-dd')<=#{date_end}

			and b.code =#{pole_code}


			and a.hitchType_id =#{hitchType_id}
