mapper.select:
    	select a.*,b.name as store_name,c.name as operater_name from ems_instore a,ems_store b,sys_user c
		where a.store_id=b.id and a.operater=c.id

			and to_char(a.operateDate,'yyyy-mm-dd') >=#{operateDate_start}


			and to_char(a.operateDate,'yyyy-mm-dd') <=#{operateDate_end}


			and a.store_id =#{store_id}
