mapper.select:
select distinct a.orderNo,store_id,orderDate,status from ems_Order a

	and a.store_id in (select store_id from sys_userstore c where c.user_id=#{user_id} and c.look='Y')

		and a.store_id=#{store_id}


		and a.orderDate >=#{date_start}


		and a.orderDate <=#{date_end}


		and a.orderNo like #{orderNo}


order by a.orderDate desc

    	SELECT distinct a.status
	    FROM ems_order a
		where a.orderNo=#{orderNo}

update ems_order 
set totalNum=${totalNum}
where id=#{order_id}
