mapper.select:
select * from ems_funrole where

	
		parentid=#{parentId}
	
	
		parentid is null

select a.*,decode(leaf,1,decode(b.navigation_id,null,0,1),null) as checked
from ems_navigation a left join ems_navigation_funrole b
on a.id=b.navigation_id and b.funRole_id=#{funRole_id}  
where

	
		 a.parentid=#{parentId}
	
	
		 a.parentid is null

select a.*,decode(leaf,1,decode(b.navigation_id,null,0,1),null) as checked
from ems_navigation a left join ems_navigation_funrole b
on a.id=b.navigation_id and b.funRole_id=#{funRole_id}  
where

	
		 a.parentid=#{parentId}
	
	
		 a.parentid is null
