mapper.select:
select max(reportcode) from sys_navigation where 

	
		parentid=#{parentId}
	
	
		parentid is null

select distinct a.* from ( select * from sys_navigation where
parentid=#{parentId}
parentid is null
) a inner join (select aa.* from sys_navigation aa, sys_navigation_funrole a, sys_funrole_user c where a.funrole_id=c.funrole_id and c.user_id=#{user_id} and aa.id=a.navigation_id ) b on INSTR(b.reportcode,a.reportcode)=1


select * from sys_navigation where

	
		parentid=#{parentId}
	
	
		parentid is null

select * from sys_navigation where

	
		parentid=#{parentId}
	
	
		parentid is null
